void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec4 textColor = texture(iChannel0, uv);
    vec4 textColor2 = texture(iChannel1, uv);

    // Create a "percentage-based" split screen effect of two colors.
    float screen1WidthPercent = 0.75;
    if (uv.x <= screen1WidthPercent) {
      fragColor = textColor;
    } else if (uv.x > screen1WidthPercent){
      fragColor = textColor2;
    }

    // Draw horizontal red line at y = 50 (in pixel units)
    if (fragCoord.y > 49.0 && fragCoord.y < 51.0) {
        fragColor = vec4(1,0,0,0.0);
    }

    // Try sampling an image, then brightening that whole image by 10%.
    // Added flickering so it could be visible.
    if (int(iTime) % 5 == 1) {
        fragColor = fragColor * 1.1;
    }
}
