{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

int imod(int a,int b) {
    float m=float(a)-floor((float(a)+0.5)/float(b))*float(b);
    return int(m+0.5);
}

bool is_prime(int n){
	if (n == 2 || n == 3) {
		return true;
	}
	if (n <= 1 || imod(n,2) == 0 || imod(n,3) == 0) {
		return false;
	}
	for (int i = 5; i * i <= n; i += 6) {
		if (imod(n,i) == 0 || imod(n,i) + 2 == 0) {
			return false;
		}
	}
	return true;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 2) {
        ivec2 p = ivec2(fragCoord) - ivec2(iResolution.xy)/2;
        int i = 0;
        if (abs(p.y) >= abs(p.x)) {
          i = 4 * p.y * p.y - p.y - p.x;
          if (p.y < p.x) {
            i = i - 2 * (p.y - p.x);
          }
        } else {
          i = 4 * p.x * p.x - p.y - p.x;
          if (p.y < p.x) {
            i = i + 2 *(p.y - p.x);
          }
        }

        fragColor = vec4(is_prime(i));
    } else {
        fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    }
}
