void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //UV MAP
    vec2 uv = fragCoord/iResolution.xy;

    //CURTAIN MOVEMENT
	float p = (sin((iTime * 1.3f) + ((uv.x + uv.y))) + 1.f) / 2.f; 

    float trs_time = mod(iTime/4.,1.0f);
    float x_step = 0.12f * trs_time;
    float y_step = 0.01f * trs_time;
    float curtain_step = 0.001f  + trs_time*0.11f;
    float mix_step = trs_time;

    //DRAW SIDE CURTAINS
    if(uv.x - (p * 0.08f) < (0.50 - trs_time) || uv.x + (p * 0.08f) > (trs_time + 0.50))
    {
        //CURTAIN COLOR
        float w = 0.f;   

        //CHECK DIRECTION/SIDE
        if(uv.x > 0.5f)
    	{
            //RIGHT
    		uv.x = ((uv.x) + (p * x_step));
    		uv.y = ((uv.y) + (p * y_step));
        	w = mod(1.f - uv.x, curtain_step) * 30.f;
    	}
    	else      
    	{
            //LEFT
    		uv.x = ((uv.x) - (p * x_step));
    		uv.y = ((uv.y) - (p * y_step));
        	w = mod(uv.x , curtain_step) * 30.f;
    	}

        //SET COLOR
        vec4 col = vec4(w,w, w,1.);  

    	fragColor = mix(texture(iChannel0,uv),col,mix_step);
       	return;
    }

    //ADD MOVIE OFFSET
  //  uv.y += sin(24.1f * iTime) * 0.001f;


        fragColor = texture(iChannel1,uv);

        return;

}
