    float Circle(vec2 uv, vec2 position, float r, float blur) 
    {
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
        return c;
    }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.xy; // координаты от 0 до 1    
    uv -= .5;  // центр круга теперь в центре экрана; координаты от -0.5 до 0.5
    uv.x *= (iResolution.x / iResolution.y); 
    // голова
    float c = Circle(uv, vec2(.0, .25), .2, .005);
    // глаза
    c -= Circle(uv, vec2(.1, .3), .05, .01);    
    c -= Circle(uv, vec2(-.1, .3), .05, .01);       
    c += Circle(uv, vec2(.1, .3), .025, .01);       
    c += Circle(uv, vec2(-.1, .3), .025, .01);       
    // уши
    c += Circle(uv, vec2(.3, .25), .15, .005);
    c += Circle(uv, vec2(-.3, .25), .15, .005);
    // тело
    c += Circle(uv, vec2(.0, -.18), .25, .005);
    //руки
    c += Circle(uv, vec2(.3, -.18), .07, .005);
    c += Circle(uv, vec2(-.3, -.18), .07, .005);
    //ноги
    c += Circle(uv, vec2(.18, -.4), .07, .005);
    c += Circle(uv, vec2(-.18, -.4), .07, .005);    

    fragColor = vec4(vec3(c), 1.0);
}
