float HorOval(vec2 uv, vec2 position, float r, float blur) 
    {
        uv.y *= (iResolution.x / iResolution.y);
        uv.x *= (iResolution.x / iResolution.y);
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
            return c;
    }
float VerOval(vec2 uv, vec2 position, float r, float blur, float l) 
    {
        uv.y *= (iResolution.x / iResolution.y);
        uv.x *= (iResolution.x / iResolution.y * l);
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
            return c;
     }
float Circle(vec2 uv, vec2 position, float r, float blur) 
     {
        uv.x *= (iResolution.x / iResolution.y);
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
             return c;
     }

void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = fragCoord.xy / iResolution.xy;    
          uv -= .5; 

     float c = VerOval(uv, vec2(.0, -.05), .4, .005, 2.5);

            c += HorOval(uv, vec2(.1, -.46), .078, .005);
            c += HorOval(uv, vec2(-.1, -.46), .078, .005);

            c += Circle(uv, vec2(+.14, -.02), .048, .005);
            c += VerOval(uv, vec2(+0.95, -.18), .17, .005, 5.5);
            c += VerOval(uv, vec2(+0.78, -.01), .17, .005, 5.5);
            c += Circle(uv, vec2(-.14, -.02), .048, .005);
            c += VerOval(uv, vec2(-0.95, -.18), .17, .005, 5.5);
            c += VerOval(uv, vec2(-0.78, -.01), .17, .005, 5.5);

            c += Circle(uv, vec2(.0, .06), .12, .005);

            c += Circle(uv, vec2(.0, .28), .15, .005);

            c += Circle(uv, vec2(-.2, .28), .11, .005);
            c += Circle(uv, vec2(.2, .28), .11, .005);
            c -= Circle(uv, vec2(.2, .28), .09, .005);
            c -= Circle(uv, vec2(-.2, .28), .09, .005);

            c -= Circle(uv, vec2(-.04, .3), .04, .005);
            c -= Circle(uv, vec2(.04, .3), .04, .005);
            c -= HorOval(uv, vec2(-.0, .35), .05, .005);



    fragColor = vec4(vec3(c)*vec3(1,1,0), 70.);
}
