#define t iTime

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy / iResolution.xy);
    uv -= vec2(0.6);
    uv.x *= iResolution.x/iResolution.y;

    // Calculate polar coordinates
    float r = length(uv);
    float a = atan(uv.y, uv.x);

    //  lines
    const float it = 10.0;
    float c = 0.0;
    for( float i = 0.0 ; i < it ; i += 1.0 )
    {
        float i01 = i / it;
        float rnd = texture( iChannel0, vec2(i01)).x;    

        float c1 = (uv.y + 1. ) * 0.004 * abs( 1.5 / sin( (cos(uv.x + uv.y) + cos(t * 0.5)) + sin(uv.y * 4.0 * rnd + rnd * 7.0 + iTime * 0.75)) );
        c = clamp(c + c1, 0.0, 1.0);
    }

    float s = 0.0;
    const float it2 = 200.0;
    for( float i = 0.0 ; i < it2 ; i += 1.0 )
    {
        float i01 = i / it2;       
        float react =  texture( iChannel1, vec2(i01, 0.0) ).x;  
        vec2 rnd = texture( iChannel0, vec2(i01)).xy;
        vec2 rnd2 = rnd - 0.15;

        rnd2 = vec2(sin(rnd2.x * 200.0 + rnd2.x * iTime * 0.2), - 0.9 * sin(rnd2.y * rnd2.x * 200.0 + iTime  * rnd2.y * 0.5));

        float r1 = 1.0 - length(uv - rnd2 * 2.);
        float rad = ( 1.0 - clamp(0.03 * rnd.y + react * 0.05, 0.0, 1.0) );

        r1 = smoothstep(rad, rad + 0.015, r1);
        s += r1;
    }



    // color mixing lines backgrounds
    vec3 bg = mix( vec3(0., 0.11, 0.12), vec3(0., 0.04, 0.04), r);
    bg = mix(bg, mix( vec3(0.25, 0.69, 0.91), vec3(0.5, 0.4, 0.4), -c), c);
    bg = mix(bg, vec3(0.75, 0.95, 1.), s );

    fragColor = vec4(bg, 1.0);
}
