float sdHexagon(vec2 p, float r) {
  /* -cos(PI/6), sin(PI/6), tan(PI/6) */
  const vec3 k = vec3(-0.866025404,0.5,0.577350269);

  /* rotate by 30 degrees for a pointy tipped hexagon */
  p = vec2(dot(vec2(-k.x,k.y),p), dot(-k.yx, p));

  /* fold space into first quadrant */ 
  p = abs(p);

  /* reflect around pi/6 plane */
  p -= 2.0*min(dot(k.xy,p),0.0)*k.xy;

  /* take out the chunk of the shape we want */
  p -= vec2(clamp(p.x, -k.z*r, k.z*r), r);
  return length(p)*sign(p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

  float d = sdHexagon(p, 0.5);

  vec3 col = vec3(1.0 - smoothstep(0.0, 0.1, d));
  // col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

  fragColor = vec4(col,1.0);
}
