void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = fragCoord.xy/min(iResolution.x, iResolution.y);
    float col =FBM24_2(p*5.,1.0,1.0);
    vec3 tcol;
    if(col<0.25)
    {
        tcol = vec3(0.0,0.7,1.0);
    }
    else if(col<0.35)
    {
        tcol = vec3(0.87, 0.72, 0.5);
    }
    else if(col<0.5)
    {
        tcol = vec3(0.13, 0.54, 0.13);
    }
    else
    {
        tcol = vec3(0.7, 0.5, 0.2);
    }
    fragColor = vec4(tcol, 1.0);
}

float hash12(vec2 p) 
{
	vec3 p3 = fract(vec3(p.xyx) * .1031);
	p3 += dot(p3, p3.yzx + 33.33);
	return fract((p3.x + p3.y) * p3.z);
}
float ValueNoise21(vec2 p) 
{
    vec2 i = floor(p);
    float v00 = hash12(i);
    float v01 = hash12(i + vec2(0.0, 1.0));
    float v10 = hash12(i + vec2(1.0, 0.0));
    float v11 = hash12(i + vec2(1.0, 1.0));
    vec2 f = fract(p);
    f = f * f * (3.0 -2.0 * f); 
    return mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y)-0.5;
}
float FBM24_2(vec2 uv, float amplitude, float frequency)
{
	float result = 0.;
	float amplitude2 = amplitude;
	float frequency2 = frequency;
	for (int i = 0; i < 4; i++)
	{
        result += ValueNoise21(uv * frequency2) * amplitude2;
		amplitude2 *= 0.5;
		frequency2 *= 2.0;
	}
	return result;
}
