#define pi 3.14159265
#define tau pi * 2.0

float line(vec2 uv,float rad,float blur,vec2 p1,vec2 p2){
  rad = 1.0-rad*0.025;

  float d1 = distance(p1,p2);
  float d2 = distance(p1,uv);

  float segment = distance(mix(p1,p2,clamp(d2/d1,0.0,1.0)),uv);

  return smoothstep(rad*blur,rad,1.0-segment);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
  vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
  vec3 col = vec3(0.0);

  float angle = iTime;
  float num = 5.0;
  vec2 v2 = vec2(0.0);
  for(float i=0.0;i<num+1.0;i++){
  	float theta = tau/num * i+angle;
  	vec2 v1 = vec2(sin(theta),cos(theta));
  	if(i==0.0) v2 = v1;
  	col += line(uv,0.1,0.96,v1/5.0,v2/5.0) * vec3(0,1,1);
    v2 = v1;
  }

  fragColor = vec4(col,1.0);
}
