void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;  
    float y0 = min(.2/abs(U.y - U.x), 1.);
    float y1 = min(.2/abs(U.y + U.x), 1.);
    float y2 = min(.2/abs(U.y - 0.2), 1.);
    O = vec4(mix(y0 * y1 * y2, y0 + y1 + y2, 0.5 * cos(iTime) + .5));
}
