#define KEYBOARD iChannel1
#define KEY_RESET 82
//#define hash(p)  fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453)

float inv(float y) {
    return 0.5 - sin(asin(1.0-2.0*y)/3.0);
}

float Cell(in ivec2 p) {
    // do wrapping
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p+r) % r;

    // fetch texel
    return texelFetch(iChannel0, p, 0 ).x;
}


float hash1(float n) {
    return fract(sin(n)*138.5453123);
}

bool key_down(int key) {
    return int(texelFetch(KEYBOARD, ivec2(key, 0), 0).x) == 1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    ivec2 px = ivec2( fragCoord );

    if (key_down(KEY_RESET) || iFrame==0) 
    {    
        float d = length((fragCoord -.5* iResolution.xy ) / iResolution.y);
        float g = hash1(fragCoord.x * 13.0  + 10.131 * iTime + 100.19414 + hash1(fragCoord.y*73.1 + 1931.1));
        g = step(0.99, g);
        //g *= 1.-step(d, 10./iResolution.y);
        fragColor = vec4(g, 0.0, 0.0, 0.0 );
        return;
    }

    // center cell
    float e = Cell(px); 

    // neighbour cells
    float t = Cell(px + ivec2(0,-1));
    float b = Cell(px + ivec2(0,1));
    float l = Cell(px + ivec2(-1,0));
    float r = Cell(px + ivec2(1,0));   

    // "average" of neighbours
    float k = 0.5 * max(t + b, l + r);

    // difference between "average" and center
    float j = abs(e - k);

   // e += 0.3 * j - 0.06;
     float a = 0.0;
    if (e > k - 0.35)
        e = 0.02 + 0.89 * k - 0.5 * j * k * k;
   // else
     //   e *= 1.5 * k;

    if (e <= k * 2.0 * j)// && e <= 1. / 20.)
        e = k * 1.545;
    else if (k >= 0.9)
        e = k + 0.1 - 0.5 * (1.-e);
    //else if (e >= 1.)
      //  e = 0.;

    e = clamp(e,0.,1.);

    fragColor = vec4( e, 0.0, 0.0, 0.0 );
}

#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+0.)
//#define sabs(x, k) sqrt(x*x+k)-0.1

#define Rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float cc(float a, float b) {
    float f = thc(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float cs(float a, float b) {
    float f = ths(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

float smin(float a, float b) {
    float k = 0.12;
    float h = clamp(0.5 + 0.5 * (b-a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);
}
