void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float c = 2.*iTime;

    vec2 t1 = 0.3*vec2(2.*cos(c/2.),sin(c));
    vec2 t2 = 0.3*vec2(1.-cos(c)-2.,sin(-1.*c));
    vec2 t3 = 0.3*vec2(cos(c)+1.,sin(c));
    float r = 0.1;

    vec3 col = vec3(0.);
    col.x = smoothstep(r,r-0.01,length(uv+t1));
    col.y = smoothstep(r,r-0.01,length(uv+t2));
    col.z = smoothstep(r,r-0.01,length(uv+t3));

    // Output to screen
    fragColor = vec4(col,1.0);
}
