#define NUM_SAMPLES 100.0
#define USE_GOLDEN_RATIO
#define EVEN_SPACED
#define POS_TAPER 0.4
#define CIRCLE_TAPER 0.5
#define CIRCLE_SIZE 1.3

float circle(in vec2 uv, in vec2 p, in float rad)
{
    vec2 puv = uv - p;
    float rsquare = rad*rad;
    return smoothstep(rsquare + 0.000005, rsquare - 0.000005, dot(puv, puv));
}

/**
 * radius function for the i'th sample, given a taper t.
 */
float radius(float i, float t)
{
    #ifdef EVEN_SPACED
        return pow((i + 0.5) / NUM_SAMPLES, t) * 0.5;
    #else
        return ((i + 0.5) / NUM_SAMPLES) * 0.5;
    #endif
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = radians(180.0);
    float aspect = iResolution.y / iResolution.x;
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= aspect;

    vec3 col = vec3(.5, .5, .5);

    for(float i = 0.0; i < NUM_SAMPLES; i += 1.0) {
        float r = radius(i, POS_TAPER);
        #ifdef USE_GOLDEN_RATIO
            float theta = pi * (1.0 + sqrt(5.0)) * i;
        #else
            float theta = pi / 0.931 * i; // some interesting random ratio
        #endif
        float d = radius(i+1.0, CIRCLE_TAPER) - radius(i, CIRCLE_TAPER);
        vec2 p2 = vec2(r * cos(theta), r * sin(theta)) * aspect + vec2(0.5, 0.5 * aspect);
        float plot = circle(uv, p2, CIRCLE_SIZE * d);
        col = mix(col, vec3(1.0, 1.0, 1.0), plot);
    }

    fragColor = vec4(col,1.0);
}
