// The bare-minimum to draw a 'glowing' line in 2D. 
// Only intended as demonstration 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;

    // Calculate light intensity based on distance from 
    // center
    float light = 0.01 / abs(uv.x);

    // Some yellow tone
    vec3 color = vec3(1.0,0.8,0.4);

    // Output to screen
    fragColor = vec4(color * light,1.0);
}
