vec2 Random_points(vec2 p){
    vec3 a = fract(p.xyx * vec3(123.34, 234.34, 345.65));
    a += dot(a, a + 35.);
    return fract(vec2(a.x * a.y, a.y * a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2. * fragCoord - iResolution.xy)/iResolution.y;


    float m = 0.;
    float t = iTime * .5; 
    float minlen = 100.;
    float cellpos = 0.;

    // Time varying pixel color
    vec3 col = vec3(minlen);

    if(false) {
    for(float i = 0.; i<50. ; i++) {
        vec2 n = Random_points(vec2(i));
        vec2 p = sin(n * t);

        float d = length(uv-p);
        m += smoothstep(.02, .01, d);

        if (d<minlen) {
            minlen = d;
            cellpos = i;

        }

    }
    } else {
        uv *= 3.;
        vec2 gv = fract(uv)-.5;
        vec2 id = floor(uv);
        vec2 cellid = vec2(0);

        for(float y = -1.; y <= 1.; y++){
            for(float x = -1.; x <= 1.; x++){
            // making an offset vector
                vec2 v = vec2(x, y);
                vec2 n = Random_points(id + v);
                vec2 p = v + sin(n * t) * .5;
                float d = length(gv-p);
                if (d<minlen) {
                    minlen = d;
                    cellid = id + v;
                    //cellpos = i;

                }

            }

        }

        col = vec3(minlen);
        col.rg = cellid * .1;
    }



    // Output to screen
    fragColor = vec4(col,1.0);
}
