vec2 rotate(vec2 c, float a) {
  return vec2(c.x*cos(a)-c.y*sin(a),c.x*sin(a)+c.y*cos(a));
}
void mainImage( out vec4 color, in vec2 c ) {
    c-=iResolution.xy/2.0;
    if(max(iResolution.x,iResolution.y)<1000.0)c*=2.0;
    float r=sqrt(c.x*c.x+c.y*c.y)/256.0;
    c.x=mod(c.x,256.0)-128.0;
    c.y=mod(c.y,256.0)-128.0;
    c=rotate(c,-iTime/2.0-abs(c.x/2.0)-abs(c.y/2.0));
    // c/=2.5; //try it
    color=vec4(int(abs(c.x)<60.0&&abs(c.y)<60.0));
    if(c.x<-12.0&&c.x>-36.0&&c.y>12.0)color=vec4(0.0);
    if(c.x<36.0&&c.x>12.0&&c.y<-12.0)color=vec4(0.0);
    if(c.y>12.0&&c.y<36.0&&c.x>12.0)color=vec4(0.0);
    if(c.y<-12.0&&c.y>-36.0&&c.x<-12.0)color=vec4(0.0);
    if(color.x*color.y*color.z>=1.0) {
        color=vec4(abs(sin(r-1.0)),abs(sin(r)),abs(sin(r+1.0)),0);
    }
}
