float erp(float x, float k)
{
    return x < 0.5 ? pow(2.0*x, k)*0.5  : 1. - pow(2.0*(1.0-x), k) / 2.;
}


vec2 hash(vec2 p)
{
    vec3 p3 = fract(p.xyx * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float perlinNoise(vec2 p, float s)
{
    vec2 tlVal = hash(vec2(floor(p.x),  ceil(p.y)));
    vec2 blVal = hash(vec2(floor(p.x), floor(p.y)));
    vec2 trVal = hash(vec2( ceil(p.x),  ceil(p.y)));
    vec2 brVal = hash(vec2( ceil(p.x), floor(p.y)));

    vec2 tlOffset = p - vec2(floor(p.x),  ceil(p.y));
    vec2 blOffset = p - vec2(floor(p.x), floor(p.y));
    vec2 trOffset = p - vec2( ceil(p.x),  ceil(p.y));
    vec2 brOffset = p - vec2( ceil(p.x), floor(p.y));

    float tl = dot(tlOffset, tlVal);
    float bl = dot(blOffset, blVal);
    float tr = dot(trOffset, trVal);
    float br = dot(brOffset, brVal);

    float a = mix(bl, tl, erp(fract(p.y), s));
    float b = mix(br, tr, erp(fract(p.y), s));

    return mix(a, b, erp(fract(p.x), s)) / 2.0 + 0.5;
}



float fbm(in vec2 x, float s, in float H)
{    
    float G = exp2(-H);
    float f = 1.0;
    float a = 1.0;
    float t = 0.0;
    for( int i=0; i<8; i++ )
    {
        t += a*perlinNoise(f*x, s);
        f *= 2.0;
        a *= G;
    }
    return t;
}

ivec3[] colors = ivec3[] (ivec3(39, 55, 71), ivec3(0, 49, 98), ivec3(0, 97, 195), ivec3(0, 211, 255), ivec3(230, 255, 230));

vec3 colorMap(float value)
{
    int colorCount = colors.length();

    float v = value * float(colorCount);

    int a = int(floor(v));
    int b = a + 1;

    float fac = fract(v);
    return mix(vec3(colors[a])*0.00392156862745, vec3(colors[b])*0.00392156862745, vec3(fac));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.y *= iResolution.y/iResolution.x;
    uv *= 0.2;

    uv.x += fbm((uv + iTime*0.01) * 1.0,        1.0, 1.0);
    uv.y += fbm((uv + iTime*0.01) * 1.0 + 10.0, 1.0, 1.0);
    uv.x += fbm((uv + iTime*0.01) * 1.0,        1.0, 1.0);
    uv.y += fbm((uv + iTime*0.01) * 1.0 + 10.0, 1.0, 1.0);
    uv.x += fbm((uv + iTime*0.01) * 1.0,        1.0, 1.0);
    uv.y += fbm((uv + iTime*0.01) * 1.0 + 1.0,  1.0, 1.0);
    uv.x += fbm((uv + iTime*0.01) * 1.0,        1.0, 1.0);
    uv.y += fbm((uv + iTime*0.01) * 1.0 + 1.0,  1.0, 1.0)*5.0;

    float value = erp(fbm(uv * 20.0, 1.0, 1.0)/2.1, 6.0);

    fragColor = vec4(colorMap(value),1.0);
    //fragColor = colors[1].xyzx;
}
