float cone(vec3 position, float radius, float halfHeight) {
    vec2 p = vec2(length(position.xz) - radius, position.y + halfHeight);
    vec2 e = vec2(-radius, 2.0 * halfHeight);
    vec2 q = p - e * clamp(dot(p, e) / dot(e, e), 0.0, 1.0);
    float d = length(q);
    if (max(q.x, q.y) > 0.0) {
        return d;
    }
    return -min(d, p.y);
}

