float y(float x) {
    //return 2.0*x;
    return (2.0 * sin(iTime*2.0) * sin(iTime*1.5)) * 10.0 * x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // where 0.0 is the center
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    //bool white = y(uv.x) > uv.y;

    vec2 circleVec = vec2(cos(-iTime + uv.x), sin(-iTime*2.0));
    circleVec += vec2(sin(iTime*(10.0*sin(iTime/500.0)) + uv.x*10.0), cos(iTime*10.0 + uv.y*1.5 + uv.x*0.8))*0.1;

    bool white = cross(vec3(circleVec, 0.0), vec3(normalize(uv), 0.0)).z > 0.0;

    // Time varying pixel color
    /*vec3 col = vec3(0.0);
    if(white) {
        col = vec3(1.0);
    }*/

    float theta = atan(uv.y / uv.x);
    theta += iTime*4.0;
    float magnitude = distance(vec2(0.0), uv);
    theta += sin(magnitude*4.0 + cos(magnitude*iTime/(80.0*(sin(iTime) + 1.1))));
    float fan2 = magnitude;
    float fan3 = magnitude;
    float fan4 = magnitude;

    magnitude += sin(theta*4.0)*0.3;
    fan2 += cos(theta*4.0 + iTime*3.0)*0.4;
    fan3 += sin(theta*4.0 + iTime*1.5)*0.2;
    fan4 += sin(theta*4.0 + 1.0 + iTime*4.0)*0.35;

    //uv.x = cos(iTime)*uv.x;
    //uv.y = sin(iTime)*uv.y;

    float value = smoothstep(0.0, 0.2, magnitude);
    vec3 col = vec3(value);
    col.r += fan2;
    col.g += fan3;
	col.b += fan4;

    if(white) {
        col = vec3(1.0) - col;
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
