vec3 line(vec2 uv , float y , float lineWidth){

   float top = step( y  , uv.y);
   float bottom = step( iResolution.y - y - lineWidth   , iResolution.y -uv.y);
   vec3 empty_color = vec3(0.0);
   vec3 white = vec3(1.0);
   vec3 yellow = vec3(1.0,0.8,0.4);

   float light =  lineWidth*0.02 / abs( uv.y  -  lineWidth/2.0 ) ;


   float c =  bottom * top ;

   return mix(empty_color , yellow * light   , c);


}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
