float rand(in vec2 st) {
  return fract(sin(dot(st.xy, vec2(13.0, 79.0))) * 2764.4437);
}

vec3 rand(in vec3 s) {
  float t = 4096.0 * sin(dot(s, vec3(17.0, 31.0, 11.0)));
  return vec3(fract(512.0 * t), fract(64.0 * t), fract(8.0 * t)) - 0.5;
}

// simplex perlin noise
float snoise(in vec3 p) {
  vec3 s = floor(p + dot(p, vec3(0.33333333333)));
  vec3 x = p - s + dot(s, vec3(0.16666666667));

  vec3 e = step(0.0, x - x.yzx);
  vec3 i1 = e * (1.0 - e.zxy);
  vec3 i2 = 1.0 - e.zxy * (1.0 - e);

  vec3 x1 = x - i1 + 0.16666666667;
  vec3 x2 = x - i2 + 0.33333333333;
  vec3 x3 = x - 0.5;

  vec4 w = vec4(dot(x, x), dot(x1, x1), dot(x2, x2), dot(x3, x3));
  w = max(0.6 - w, 0.0);
  w = pow(w, vec4(4.0));
  w *= vec4(dot(rand(s), x), dot(rand(s + i1), x1), dot(rand(s + i2), x2), dot(rand(s + 1.0), x3));

  return dot(w, vec4(30.0, 60.0, 50.0, 15.0));
}

#define PI 3.14159265359

const vec3 AZUR = vec3(0.0, 0.5, 1.0);

#define scale 50.0
#define speed 0.07

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 st = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
  st *= scale;

  vec2 vel = vec2(iTime * speed * scale);
  vec2 ipos = floor(st + vel);
  float randpos = rand(ipos);
  // magic
  float sq = snoise(vec3(ipos, randpos)) * (sin(iTime * PI * randpos + randpos * 3443.0) + randpos) * 0.3;

  vec3 color = AZUR * sq;

  fragColor = vec4(color, 1.0);
}
