void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 nodistortuv = fragCoord/iResolution.y;
    vec2 distortuv = fragCoord/iResolution.xy;


    vec3 col = texture(iChannel0, distortuv).rgb;

    float scale = 30.0;
    vec2 f = (nodistortuv * scale) - floor(scale*nodistortuv);
    col = distance(f, vec2(0.5)) > 0.4 ? vec3(0) : col;

    // Output to screen
    fragColor = vec4(col,1.0);
}
