vec3 normalize_rgb(vec3 c){
	return vec3(c.x / 255.0, c.y/ 255.0, c.z/ 255.0);
}

float map(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

float sdRoundedBox( in vec2 p, in vec2 b, in vec4 r )
{
    r.xy = (p.x>0.0)?r.xy : r.zw;
    r.x  = (p.y>0.0)?r.x  : r.y;
    vec2 q = abs(p)-b+r.x;
    return min(max(q.x,q.y),0.0) + length(max(q,0.0)) - r.x;
}

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // colors
    vec4 outline_col = vec4(normalize_rgb(vec3(224, 64, 75)), 1);
    vec4 bar_bg_col = vec4(normalize_rgb(vec3(92,92,92)),1);
    vec4 panel_bg_col = vec4(normalize_rgb(vec3(37,34,34)),1);
    vec4 bar_fill_col = vec4(normalize_rgb(vec3(224, 64, 75)), 1);

    // sizes
    float bar_length = 0.6;
    float bar_height = 0.05;
    float bar_y_offset = 0.1;
    float bar_x_offset = 0.15;
    vec4 bar_roundness = vec4(0.04);
    vec4 panel_roundness = vec4(0.03);
    vec2 panel_size = vec2(0.8, 0.2);

    // progress
    float fill_length = map(fract(iTime / 5.0), 0., 1., 0.0, bar_length);

    float d_bg = sdRoundedBox(uv, panel_size, panel_roundness);
    vec4 bg_col = (d_bg > 0.0) ? vec4(normalize_rgb(vec3(57,54,55)),1) : vec4(0,0,0,0);

    //outline
    bg_col = mix( bg_col, outline_col, 1.0-smoothstep(0.0,0.003,abs(d_bg)) );

    float d_bar_fill = sdRoundedBox(uv+vec2(bar_length-fill_length+bar_x_offset, bar_y_offset), vec2(fill_length, bar_height), bar_roundness);
    vec4 bar_fill = (d_bar_fill > 0.0) ? vec4(0,0,0,0) : bar_fill_col;

    float d_bar_bg = sdRoundedBox(uv + vec2(bar_x_offset, bar_y_offset), vec2(bar_length, bar_height), bar_roundness);
    vec4 bar_bg = (d_bar_bg <= 0.0 && d_bar_fill > 0.0) ? bar_bg_col: panel_bg_col; 

    vec4 col = bg_col + bar_fill + bar_bg;

    fragColor = col;
}
