void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv *= vec2(2.1, 5.5);

    float y = sqrt(1.0 - uv.x * uv.x);
    float offset = sin(4.0 * uv.x + iTime * 3.0);
    float yp = y + offset;
    float yn = -y + offset;

    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    float mid = (yp + yn) * 0.5;
    fragColor = vec4(col, 1.0) * smoothstep(abs(yp - yn) * 0.5, abs(yp - yn) * 0.49, abs(uv.y - mid));
}
