vec3 getFractWave(vec2 uv,float t)
{
    uv.x+=sin(t*0.1)*0.5+0.5;
    float fr0= fract(uv.x*1.5*uv.y*2.0+sin(t*0.1))*fract((1.0-uv.x)*1.5*(1.0-uv.y)*2.0+sin(t*0.1));    
    float fr1= fract(uv.x*uv.y*3.0+sin(iTime*0.05))*5.0*fract((1.0-uv.x)*(1.0-uv.y)*3.0+sin(iTime*0.05)*5.0)*1.5;
    float fr2= fract(uv.x*2.0*uv.y*2.0+sin(iTime*0.01))*10.0*fract((1.0-uv.x)*2.0*(1.0-uv.y)*2.0+sin(t*0.01)*3.0);

    vec3 col =vec3((fr0+fr1+fr2)/3.0)+(vec3(sin(t),sin(t+0.33),sin(t+0.66))*vec3(0.5));
    vec3 col2=min(vec3(0.1,0.15,0.3),vec3(sin(t+uv.x*1.5)*0.5+0.5,sin(t+uv.y*2.0)*0.5+0.5,sin(t+uv.x*3.0+uv.y*2.5)*0.5+0.5));

    return col=col*col2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t=iTime+12.5;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 tex = texture(iChannel0,uv*0.05+vec2(t*0.002,sin(t*0.05)*0.075));
    uv+=vec2(tex.r,tex.g)*abs(sin(tex.b*0.1));

    // Time varying pixel color

    vec3 col=vec3(0);
    for(int i=0;i<50;i++)
    {
        vec2 offset= vec2(i)/iResolution.xy;

        col+=getFractWave(uv+offset*sin(t*0.1)*5.0,t)/50.0;        
    }
    vec3 col2=getFractWave(uv,t);
    vec3 col3=getFractWave(uv*1.5,t*1.5);

    col=min(col,col2*2.0)+col;
    col*=sin(col+iTime*0.3)*0.5+0.5;
    col=col+col3*0.15;

    // Output to screen
    fragColor = vec4(col,1.0);
}
