// An even more readable version of https://www.shadertoy.com/view/sslBDj, giving named variables 
// and expanding some of the more dense code.

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    fragCoord *= 7.0 / iResolution.xy;
    fragColor -= fragColor;

    float vertSineWave = 3.0 + sin(iTime / 2.0);

    for (float i = -60.0; i < 9.0; i++)
    {
        float horizSineWave = vertSineWave * 0.5 * (fragCoord.x - sin(i / 6.0 + iTime));
        float blackOrWhite = mod(i, 2.0);
        float shadingSineWave = 0.7 - 0.3 * sin(horizSineWave);
        fragColor += (blackOrWhite * shadingSineWave - fragColor) * smoothstep(0.0, 15.0 / iResolution.y, 4.0 / vertSineWave * cos(horizSineWave) - i / 6.0 - fragCoord.y);
    }
}
