void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv *= 7.0;
    uv = fract(uv);

    float x = uv.x;
    float y = uv.y;

    float time = fract(iTime);

    float offset = (-abs(2.0 * (time - 0.5)) + 1.0) / 2.0;

    if (y > 0.5 && x > 0.5 - offset && y < 1.0 && x < 1.0 - offset) {
        fragColor = vec4(1.0);
        return;
    }

    if (y > 0.0 && x > 0.0 + offset && y < 0.5 && x < 0.5 + offset) {
        fragColor = vec4(1.0);
        return;
    }
    fragColor = vec4(0.0);
}
