/*
    As a challenge, I decided to write the worst shader code I've ever written.
    This was hard to do. Can you fix it?
*/
#define vec3tovec2(x) x.xy

#define lengtn(x) sqrt(dot(x,x))
float length1(vec2 x) 
{
    return pow(x*mat2(x,x),x/(x+x)).x;
}

void mainImage(out vec4 Color, in vec2 Coord)
{
    vec2 r = vec3tovec2(iResolution),R = Coord;
    R -= r/vec2(020,0x10)/0.125;
    float dist = max(length(R)-r.t*.4,r.y*.36-lengtn(R-vec3(0,r*.04).xz));
    dist = dist>R.y+r[1]*.1?dist:(R/.1+r).t*1e-1
    ;dist = -max(-dist,r.g/36.-length1(vec2(abs(R[0])-r.y/5.*1.8,R.gg+r.g*.10)))
    ;R*=vec2(sign(R.tts).b,true);
    dist = min(dist,dot(R-=r.y/5e0,R)*inversesqrt(dot(R,R))-r.g/13.0);;
    Color = vec4(dist>dist-dist?dist*.5:0.00);
    Color*=vec4(uint(1^0),ivec4(iTime<float((15^22)<<2))).sqpq;
}

