#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash22(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * HASHSCALE3);
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float res = 8.;
	vec2 uv = (fragCoord.xy / iResolution.y - .5) * res;

    uv -= 0.5;
    vec2 p0 = floor(uv);

    vec2 circles = vec2(0.);
    vec2 pi = p0;
    vec2 hsh = pi;
    vec2 p = pi + hash22(hsh);

    float t = fract(hash12(hsh));
    vec2 v = p - uv;
    float d = length(uv) - mod(iTime, 2.);

    float h = 1e-3;
    float d1 = d - h;
    float d2 = d + h;
    float p1 = sin(31.*d1);// * smoothstep(-0.6, -0.3, d1) * smoothstep(0., -0.3, d1);
    float p2 = sin(31.*d2) * smoothstep(-0.6, -0.3, d2) * smoothstep(0., -0.3, d2);
    circles += (p1) * 5.;
    circles /= 118.;

    float intensity = .3;//mix(0.01, 0.15, smoothstep(0.1, 0.6, abs(fract(0.05*iTime + 0.5)*2.-1.)));
    vec3 n = vec3(circles, sqrt(1. - dot(circles, circles)));
    vec3 color = texture(iChannel0, uv/res - intensity*n.xy).rgb + 5.*pow(clamp(dot(n, normalize(vec3(1., 0.7, 0.5))), 0., 1.), 6.);
	fragColor = vec4(color, 1.0);
}
