void mainImage( out vec4 o, in vec2 i )
{
    vec2 p = (2.*i - iResolution.xy) / iResolution.y;

    float t = iTime;

    vec2 a = vec2(sin(t)*.8,-cos(t)*.6);
    vec2 b = vec2(cos(t)*.6,-sin(t));
    vec2 c = vec2(-cos(t)+.5,sin(t));

    vec3 d = vec3(p-a,0);
    vec3 e = vec3(b-p,0);
    vec3 f = vec3(p-c,0);

    vec3 g = cross(d,e);

    o = vec4(sign(min(dot(cross(d,f),g),dot(g,cross(e,f)))));
}
