void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - iResolution.xy)/iResolution.y;
    vec2 ms = (iMouse.xy - iResolution.xy)/iResolution.y;

    float wl = 0.5 + 0.5 * sin(uv.x + (0.5 + 0.5 * sin(iTime + uv.x * 3.0)) * 1.0 * 30.5 + iTime + uv.y * 30.5);

    float pl = (1.0 - length(uv - ms)) * 2.0;

    float r = round(wl) * pl;
    float g = max(0.0, (wl * 0.60) - 0.15) * pl;
    float b = max(0.0, (wl * 0.78) - 0.15) * pl;

    fragColor = vec4(vec3(r, g, b),1.0);
}
