#define myPi 3.141592654f
#define Eps 1e-8
#define maxZ 100.0


const int n = 4;
vec3 colVec[n] = vec3[n](vec3(1.1, 0.6, 0.9), vec3(1.0, 0.7, 0.0), vec3(0.3, 0.5, 0.9),vec3(3.0, 0.5, 0.5));
vec3 BGcol = vec3(1.0, 1.0, 1.0);
//float zVec[n] = float[n](3.0, 2.0, 1.0);


int sizes[n] = int[n](3, 3, 4, 5), inds[n] = int[n](0, 3, 6, 10); const int m = 15;
vec2 polygonsXY[m]; float polygonsZ[m];

void initPolygons(out vec2 polygonsXY[m], out float  polygonsZ[m], in float time){
// 0 -- triangle
    polygonsXY[0] = vec2(0.0, 0.0); polygonsZ[0] = 0.1; 
    polygonsXY[1] = vec2(1.0, 0.0); polygonsZ[1] = 0.1;
    polygonsXY[2] = vec2(0.0, 1.0); polygonsZ[2] = 0.1;
// 1 -- triangle
    polygonsXY[3] = vec2(0.3, 0.2); polygonsZ[3] = 0.09;
    polygonsXY[4] = vec2(0.7, 0.5); polygonsZ[4] = 0.10;
    polygonsXY[5] = vec2(0.3, 0.9); polygonsZ[5] = 0.05;
// 2 -- rotated square
    float w = 2.0;
    vec3 center = vec3(0.45, 0.45, 0.1 + 0.05*sin(time*w)); float r = 0.4;
    int i; float j =0.0, phi = myPi/6.0;
    float ang = 1.5*myPi/7.0; 
    vec3 tmpVec;
    mat3 rot = mat3(vec3(1.0, 0.5, 0.0), vec3(0.0, cos(phi), sin(phi)), vec3(0.0, -sin(phi), cos(phi)) );
    for (i = 6; i < 10; i++){
        tmpVec = rot*(r*vec3(cos(j*ang + time), sin(j*ang + time), 0.0)); 
        polygonsXY[i] = tmpVec.xy + center.xy;
        polygonsZ[i] =tmpVec.z + center.z;
        j = j + 1.0;
    }



for (i = 10; i < m; i++){
        tmpVec = rot*(r*vec3(cos(j*ang + time*w), sin(j*ang + time*w), 0.0)); 
        polygonsXY[i] = tmpVec.xy + center.xy;
        polygonsZ[i] = tmpVec.z + center.z;
        j = j + 1.0;
    }

}




int leftTurn(in vec2 a, in vec2 b, in vec2 c)
{
    float tmp = (c.x - a.x)*(b.y - a.y) - (c.y - a.y)*(b.x - a.x);
    if (abs(tmp) < Eps) 
        return 0;
    else 
        return tmp > 0.0 ? 1: -1;
}


bool inPolygon(in int ind0, in int size, in vec2 vert[m], in vec2 p) // vertices in counter clockwise order
{
    bool res = leftTurn(vert[ind0 + size-1], vert[ind0], p) <= 0; int i = ind0;
    while(res && (i < ind0 + size -1))
    {
        res = res && (leftTurn(vert[i], vert[i+1], p) <= 0); i++;
    }
    return res;
}

float zInPlane(in vec3 a, in vec3 b, in vec3 c, in vec2 p) // z coordinate for (x,y) point of a plane (a-b, c-b)
{
    vec3 nrm = cross(a-b, c-b);
    //float D = - dot(nrm, b);
    if (abs(nrm.z) < Eps) return a.z;
    else return (dot(nrm, b) - nrm.x*p.x - nrm.y*p.y)/nrm.z;
}


// vec3 normalize(vec3), vec3 cross(vec3, vec3) are predefined


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime, tmp; //iTime;
    initPolygons(polygonsXY, polygonsZ, time);
    vec3 col = BGcol; 
    float z = maxZ; 
    int i;
    for (i =0; i < n; i++){
        if ( inPolygon(inds[i], sizes[i], polygonsXY, uv) ){
            tmp = zInPlane(vec3(polygonsXY[inds[i]].x, polygonsXY[inds[i]].y, polygonsZ[inds[i]]), \
            vec3(polygonsXY[inds[i]+1].x, polygonsXY[inds[i]+1].y, polygonsZ[inds[i]+1]), \
            vec3(polygonsXY[inds[i]+2].x, polygonsXY[inds[i]+2].y, polygonsZ[inds[i]+4]), uv);
            if (tmp < z){
                z = tmp; col = colVec[i]+0.2*sin(time); 
            }
        }
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
