uint rotl(uint x, uint k) {
    return (x << k) | (x >> (32u - k));
}
// http://xoshiro.di.unimi.it/
uint next(inout uvec4 s) {
    uint result = s[0] + s[3];
	uint t = s[1] << 9;
	s[2] ^= s[0];
	s[3] ^= s[1];
	s[1] ^= s[2];
	s[0] ^= s[3];
	s[2] ^= t;
	s[3] = rotl(s[3], 11u);
	return result;
}

vec4 tofloat(uvec4 u) {
    return uintBitsToFloat(0x3f800000u | u >> 9) - 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    uvec4 s = uvec4(fragCoord,fragCoord);
    s^=s.wzyx;
    s*=uvec4(0x47a94a59,0x1ba8af96,0x270179da,0xca95f002);
    s^=s.wzyx;
    fragColor = tofloat((uvec4(next(s),next(s),next(s),next(s))));
}
