const float n = 7.,
    PI = 3.14;

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

void mainImage( out vec4 O, in vec2 u ) {
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    O = vec4(1.);
    for(float i = 0.; i < n; i++) {
        float angle = i * PI/n + PI/4. + i * iTime/10.;
        vec2 rotU = rotZ(U, angle);
        O *= vec4(min(.4/abs(rotU.y - rotU.x), (2. - cos(iTime))));
    }
}
