//https://en.wikipedia.org/wiki/Norm_(mathematics)#p-norm

//xy scaling factors
#define a .7
#define b 1.

//
#define p 2.
#define value 1.

//transition width parameter
#define w .10

float unrooted_norm(vec2 x, vec2 scale, float exponent)
{
    return scale.x*pow(abs(x.x), exponent) + scale.y*pow(abs(x.y), exponent);
}

void mainImage( out vec4 O, in vec2 U)
{
    vec2 R = iResolution.xy;
    vec2 uv = U -.5*R; uv /= .5*R.y;
    float t = iTime;
    t = mod(t, 3.);

    float col = smoothstep(value+w,value-w,unrooted_norm(uv, vec2(a,b), .15/t)); //tv switch off effect
    //float col = smoothstep(value+w,value-w,unrooted_norm(uv, vec2(a,b), p));

    //float col = smoothstep(1.+w,1.-w,unrooted_norm(uv, vec2(1.,1.), .5));  //astroid
    //float col = smoothstep(1.+w,1.-w,unrooted_norm(uv, vec2(1.,1.), 1.));  //square
    //float col = smoothstep(1.+w,1.-w,unrooted_norm(uv, vec2(1.,1.), 2.));  //circle
    //float col = smoothstep(1.+w,1.-w,unrooted_norm(uv, vec2(1.,1.), 4.));  //

    //float col = smoothstep(1.+w,1.-w,unrooted_norm(uv, vec2(1.,-1.), .5));  //
    //float col = smoothstep(1.+w,1.-w,unrooted_norm(uv, vec2(1.,-1.), 1.));  //
    //float col = smoothstep(1.+w,1.-w,unrooted_norm(uv, vec2(1.,-1.), 2.));  //hyperbola
    //float col = smoothstep(1.+w,1.-w,unrooted_norm(uv, vec2(1.,-1.), 4.));  //
    O = vec4(col);
}
