// variant of https://shadertoy.com/view/flG3Ry

// === morphological skeleton. =============================
//     also named topological skeleton or medial axis.
// to get the ridge of SDF ( i.e. valley ), we seek for the max variation of the SDF gradient 

float z = 4./360.;                     // characters size

void mainImage( out vec4 O, vec2 U ) {
    if (iMouse.z>0.) z /= .5 + 4.*iMouse.y/iResolution.y; // mouse zoom
    U *= z;

 #define T(U)   texture(iChannel0, ( U + mod(iTime/vec2(1,16.),16.) ) / 16.)
 #define Tc(U)  T(U) * step(T(U).w,.51) // clamp SDF to mask + epsilon
                           // SDF
    O = Tc(U) + Tc(U+8.5);              // lost space: insert more chars

    O = O.x - length(fwidth(O.yz))/z/50. * vec4(0,1,1,0);
     // mask                SDF gradient
             // max on the ridge of SDF gradient ( = skeleton )
}
