#define HIGHP

//shades of lava
#define S4 vec3(100.0, 0.0, 0.0) / 100.0
#define S3 vec3(99.0, 19.0, 5.0) / 100.0
#define S2 vec3(100.0, 93.0, 49.0) / 100.0
#define S1 vec3(100.0, 45.0, 25.0) / 100.0
#define NSCALE 200.0 / 2.0

#define u_texture iChannel0
#define u_noise iChannel1

#define u_campos iMouse.xy
#define u_resolution iResolution.xy
#define u_time iTime * 60.0

#define v_texCoords vec2(fragCoord / 6000.0)
#define texture2D texture

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = v_texCoords.xy;
    vec2 coords = vec2(c.x * u_resolution.x + u_campos.x, c.y * u_resolution.y + u_campos.y);

    float btime = u_time / 10000.0;
    float wave = (sin(coords.x * 1.1 + coords.y) + 0.1 * sin(2.5 * coords.x) + 0.15 * sin(3.0 * coords.y)) / 30.0;
    float noise = wave + (texture2D(u_noise, (coords) / NSCALE + vec2(btime) * vec2(-0.2, 0.8)).r + texture2D(u_noise, (coords) / NSCALE + vec2(btime * 1.1) * vec2(0.8, -1.0)).r) / 2.0;
    vec4 color = texture2D(u_texture, c);

    if(noise > 0.54 && noise < 0.57){
        color.rgb = S2;
    }else if (noise > 0.49 && noise < 0.62){
        color.rgb = S1; //orange
    }else if (noise > 0.39 && noise < 0.65){
        color.rgb = S3; //red
    }else{
        float nt = 0.85 * (1.0 - abs(0.56 - noise));
        color.rgb += S4 * nt * nt * nt;
    }

    fragColor = color;
}
