float rand(vec2 p ){
    return fract(sin(dot(p ,vec2(12.9898,78.233))) * 43758.5453);
}

float smooth_line(float x) {
    float f = 6.0 * pow( x , 5.0 ) -  15.0 * pow( x , 4.0 ) +  10.0 * pow( x , 3.0);
    return f;
}

float valueNoise(vec2 pos){
    vec2 fr= fract(pos);
    vec2 AA= floor(pos);
    vec2 AB= floor(pos)  + vec2(1,0);
    vec2 BA= floor(pos)  + vec2(0,1);
    vec2 BB= floor(pos)  + vec2(1,1);

    float rAA = rand(AA);
    float rAB = rand(AB);
    float rBA = rand(BA);
    float rBB = rand(BB);

    // bilinear interpolation of the value
    float nAA_AB = mix(rAA , rAB,          (   fr.x));
    float nBA_BB = mix(rBA , rBB,      smooth_line   (  fr.x));
    return mix(nAA_AB , nBA_BB,      smooth_line (  fr.y));
}

