Main
{
    Q = vec4(0);
    float d = 1e9;
    for (float j = 0.; j < M; j++)
    for (float i = 0.; i < N; i+=3.)
    {
        vec4 a = A(vec2(i,j));
        vec4 b = A(vec2(i+3.,j));
        float l = ln(U,a.xy,b.xy);
        if (l < d) {
            d = l;
            Q = (.5+.5*sin(.1*iTime+j+vec4(1,2,3,4)))*
            smoothstep(3.,2.,abs(l-8.));
            Q += smoothstep(4.,3.,l);
        }

    }
    Q += .28*(
        exp(-.01*U.x)+exp(-.01*U.y)+
        exp(-.01*(R.x-U.x))+exp(-.01*(R.y-U.y))
        );
        Q = 1.-Q;
}

#define R iResolution.xy
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define N 100.
#define M 8.
#define Main void mainImage(out vec4 Q, in vec2 U)


float ln (vec2 p, vec2 a, vec2 b)
{
    return distance(p,a+(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

vec2 shape (float i, float j) {
    return vec2(
        (.5+.4*sin(1.6*j+6.*iTime))*(i/N*.25+.25)*R.x,
        R.y*(.5+.05*i/N*sin(4.*i/N*6.2))
    );
}
Main
{
    if (U.y>M) discard;
    Q = A(U);
    {

        vec2 f = vec2(0);
        for (float j=0.;j<M;j++)
        for (float i=0.;i<N;i++)
        {
            if (j==floor(U.y)) {
                vec4 a = A(vec2(i,U.y));
                vec2 r1 = a.xy-Q.xy;
                vec2 r2 = shape(i+.5,U.y)-shape(U.x,U.y);
                float l1 = length(r1);
                float l2 = length(r2);
                if (l1>0.)
                    f += r1/l1/N*(l1-l2);
            } else {
                vec4 a = A(vec2(i,j));
                vec2 r = a.xy-Q.xy;
                float l = length(r);
                if (l>0.)
                    f -= r/l*exp(-l*l/20./20.);
            }
        }
        Q.zw += f;
        Q.xy += f+Q.zw;
        if (Q.x<1.)Q.x=1.,Q.z*=-1.,Q.w*=0.;
        if (Q.y<1.)Q.y=1.,Q.w*=-1.,Q.z*=0.;
        if (Q.x>R.x)Q.x=R.x,Q.z*=-1.,Q.w*=0.;
        if (Q.y>R.y)Q.y=R.y,Q.w*=-1.,Q.z*=0.;
    }

    if (iMouse.z>0.){
        vec2 r = iMouse.xy-Q.xy;
        Q.zw -= 1e-2*r*exp(-5.*length(r)/R.x);
    }

    if (iFrame < 1)
    {

        Q.xy = shape(U.x,U.y);
        Q.y += 100.*U.y;
        Q.z = 0.;
        Q.w = 0.;
    }

}

