float random(float income){
    return fract( (sin(income) * 4698.0101255) + (sin(income) * 29189.92918) + (sin(income) * 8.327) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 1.1;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(sin(random(uv.x * (iTime + 250.0) - uv.y * (iTime + 2125.0))), sin(random(uv.x * (iTime + 80.0) + uv.y * (iTime + 1.0) )), sin(random(uv.x * (iTime + 8.0) - uv.y * (iTime + 32.0) )));

    // Output to screen
    fragColor = vec4(col,1.0);
}
