mat2 rot(float theta){
    return mat2(cos(theta),sin(theta),-sin(theta),cos(theta));
}
vec3 white = vec3(1.);
vec3 black = vec3(0.);

vec2 toPolar(vec2 uv){
    return vec2(length(uv),atan(uv.y,uv.x));
}
vec2 toCart(vec2 po){
    return vec2(po.x*cos(po.y),po.x * sin(po.y));
}
vec3 f(vec2 uv){
    float d = length(uv-.5);
    vec3 col1 = (d < (.5 + .5*cos(iTime))) ? black : white;
    vec3 col2 = (d < (.25 + .5*sin(iTime))) ? white : black;
    return mix(col1,col2,.7*abs(cos(d))+.3*abs(sin(32.*d)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    uv *= .25;
    uv *= rot(iTime/4.);

    vec2 po = toPolar(uv);
    po.x = 1./(po.x+.25-.25*cos(iTime));
    po.x = po.x + (1. / po.x);
    po.y = po.y + (.25*sin(iTime) + cos(iTime)*uv.x + .25*cos(iTime) + sin(iTime)*uv.y);
    uv = toCart(po);

    uv = mod(uv,2.);
    uv = rot(iTime)*uv;

    vec3 col = vec3(0.);
    col.x += (.5 + .5*cos(2.13*iTime))*length(f(vec2(uv.x,abs(uv.y))));
    uv = rot(iTime) * uv;
    col.y += (.5 + .5*cos(1.8*iTime))*length(f(vec2(abs(uv.x),uv.y)));
    uv*= rot(iTime);
    col.z += (.5 + .5*sin(.75*iTime))*length(f(abs(uv)));

    fragColor = vec4(col,1.0);
}
