void mainImage(out vec4 fragColor, vec2 fragCoord)
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / max(iResolution.x, iResolution.y);

    float dist = length(uv);
    if (dist < 0.2)
        fragColor = vec4(1, 0, 0, 1);
    else if (dist < 1.0)
        fragColor = vec4(0, 0, 0, 1);
    else
        fragColor = vec4(1);
}
