//----------------------------------------------------------------------
// Crashed_Plant.glsl               2022-09-01
// Ray marcher created by Inigo Quilez - iq/2013
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// More info here: https://iquilezles.org/articles/distfunctions
// flower: https://www.shadertoy.com/view/MltSRf
// tags: 3d, raycasting, atan, animation, plant
//----------------------------------------------------------------------

const float radius = 0.7;
const float leaves = 16.0;
const float torsion = 1.7;
      float twist = 3.0;
const vec3 iy = vec3(0,1,0);

//----------------------------------------------------------------------
float plant0(vec3 p)
{
  vec3 n = 1.5*normalize(p);
  float q = length(p);
  float rho = atan(length(vec2(n.x,n.z)),n.y)*20.0+q*15.0;    // vertical part of  cartesian to polar with some q warp
  float theta = atan(n.x,n.z)*leaves +rho*torsion +p.y*twist; // horizontal part plus some warp by z and rho
  float k = 0.6 *(1.3 - abs(dot(n,iy)));  // 1.3-abs(dot()) is limiting the warp effect at poles
  float turn = iTime*0.2;
  return q -radius -k*(sin(theta)+sin(rho-turn));
}
//----------------------------------------------------------------------
float plant1(vec3 p)
{
  float xy = sqrt(p.x*p.x + p.y*p.y);
  float turn = iTime*0.5;
  return pow(xy-2.,2.) 
     + 33.*sin(turn)+1111.*cos(12.*atan(p.x, p.y)) * sin(18.*atan(p.z, xy))*0.08
	 -5./(p.x*p.x+p.y*p.y+pow(p.z,4.)) +p.z*p.z; 
}
//----------------------------------------------------------------------
// get scenery value and color
vec2 map( in vec3 pos )
{
  return vec2(plant1(pos),20.7);
}

// do ray marching
vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;
	float precis = 0.01;
    float t = tmin;
    float m = -1.0;
    for( int i=0; i < 400; i++ )
    {
	    vec2 res = map( ro+rd*t );
        if((res.x < precis) || (t > tmax)) break;
        t += res.x*0.05;
	    m = res.y;
    }
    if( t > tmax ) m=-1.0;
    return vec2( t, m );
}

// calculate soft shadow value
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i < 16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// calulate normalized surface normal vector
vec3 calcNormal( in vec3 pos )
{
	vec3 eps = vec3( 0.001, 0.0, 0.0 );
    float d = map(pos).x;
	vec3 nor = vec3(
	    map(pos+eps.xyy).x - d,
	    map(pos+eps.yxy).x - d,
	    map(pos+eps.yyx).x - d );
	return normalize(nor);
}

// calculate Ambient Occlusion
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<15; i++ )
    {
        float hr = 0.05 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

// get color of given ray
vec3 render( in vec3 ro, in vec3 rd )
{ 
    vec3 col = vec3(0.7, 0.9, 1.0) +rd.y*0.8;
    vec2 res = castRay(ro,rd);
    float t = res.x;
	float m = res.y;
    if( m > -0.5 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal( pos );
        vec3 ref = reflect( rd, nor );

        // material        
		col = 0.50 + 0.3*sin( vec3(2.3-pos.y/4.0, 2.15-pos.y/12.0,-1.30)*(m-1.0) );

        if( m < 1.5 )
        {
            float f = mod( floor(5.0*pos.z) + floor(5.0*pos.x), 2.0);
            col = 0.4 + 0.1*f*vec3(1.0);
        }

        // lighting        
        float occ = calcAO( pos, nor ) ;
		vec3 light = normalize( vec3(-0.6, 0.7, -0.5) );
		float amb = 0.1;  // clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, light ), 0.0, 1.0 );
        float bac = 0.0;  // clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = 0.7;  //pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = 0.0;  //pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

        dif *= 0.5 +0.5*softshadow( pos, light, 0.02, 2.5 );
        //dom *= softshadow( pos, ref, 0.02, 2.5 );

		vec3 lin = vec3(0.0);
        lin += 1.20*dif*vec3(1.00,0.85,0.55);
		lin += 1.20*spe*vec3(1.00,0.85,0.55)*dif;
        lin += 0.20*amb*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*dom*vec3(0.50,0.70,1.00)*occ;
        lin += 0.30*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.40*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;
    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.002*t*t ) );
    }
	return vec3( clamp(col,0.0,1.0) );
}

// set camera transformation matrix
mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ratio = vec2(iResolution.x / iResolution.y, 1.0); // aspect ratio
    vec2 pos = fragCoord.xy / iResolution.xy;      //  0.0 .. 1.0
    vec2 uv = (2.0 * pos - 1.0) * ratio;           // -1.0 .. 1.0
    vec2 mpos = iMouse.xy / iResolution.xy;        //  0.0 .. 1.0  

	// ray values	
    vec3 ro = vec3(0.0,4.0*cos(-3.6*mpos.y),4.0);  // ray origin
 	vec3 ta = vec3(-0.1, 0.0, 0.10);               // ray target 
    mat3 ca = setCamera(ro, ta, 0.0);              // camera-to-world transformation
	vec3 rd = ca * normalize(vec3(uv, 2.0));       // ray direction

    // render 	
    //twist = 11.0 * sin(iTime);      // twisting...
    vec3 col = render( ro, rd );
	col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}
