mat2 rotate2D(float r)
{
	return mat2(10.0 + cos(r) / sin(r), -sin(r) * 20.0, sin(r) * 4.0 + cos(r) / 2.0, cos(r * 10.0));
}

float random(float seed)
{
	return fract(sin(seed)*1e4);
}

float row(float x, float y, float row_idx)
{
	const float ANTIALIZE_FACTOR = 0.15;

	float rand_factor = 0.25+random(row_idx)*0.75;
	float color = step(0.2, y) * step(y, 0.8) * step(0.5, random(floor(x*rand_factor + 15.*iTime*random(rand_factor))));
	float antialiser = smoothstep(0.2, 0.2+ANTIALIZE_FACTOR, y) * smoothstep(0.8, 0.8-ANTIALIZE_FACTOR, y);
	return color * antialiser;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = (2.*fragCoord.xy - iResolution.xy) / iResolution.y;
	uv *= rotate2D(radians(uv.x*15.));
	uv.x *= 25.0;
	uv.y *= 35.0;

	float c = row(uv.x, fract(uv.y), floor(uv.y));

	fragColor = vec4(mix(vec3(.2, .6, .8), vec3(0.), 1.0-c), 1.);
}
