vec3 hsv2rgb(vec3 c)
{
    vec3 rgb = clamp(abs(mod(c.x * 6. + vec3(0,4,2),6.)-3.)-1.,0.,1.);
    return c.z * mix(vec3(1), rgb, c.y);
}

float circle(vec2 uv, vec2 c, float r)
{
    return step(length(uv-c),r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;

    //uv.y += 1.;
    uv *= 30.;
    //uv.x += iTime;

    vec2 i = floor(uv);
    i.y += sin(i.x*.2+iTime)*5.;
    vec2 f = fract(uv+vec2(0,sin(i.x*.4+iTime)*2.));

    float pct = circle(f,vec2(.5),sin(length(i)+iTime));

    // Time varying pixel color
    vec3 col = hsv2rgb(vec3(i.x*.05+iTime*.27,sin((i.y*5.+sin(-iTime+i.x*.7)*3.)*.08+iTime)*.5+.5,1)) * pct;//0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
