#ifdef GL_ES
precision mediump float;
#endif




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 st = fragCoord.xy/iResolution.xy;
  st.x *= (iResolution.x/0.45)/(iResolution.y/0.25);
  vec3 color = vec3(0.0);
  float d = 0.0;
  float d2 = 0.0;
  float d3 = 0.0;

  // Remap the space to -1. to 1.
  st = st *2.-1.;

  // Make the distance field
  d2 = length( abs(st*iTime)-0.9 );
  d3 = length( min(abs(st)-0.1*abs(tan(0.1*iTime)),0.1) );
  d = length( max(abs(st)-0.0003,0.005) );
  // Visualize the distance field
  fragColor = vec4(vec3(acos(tan(d2/d3*0.5*d)),acos(cos(d/d3*0.5*d)),acos(sin(d2/d3*0.5*d))),0.8);


}
