void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec4 inputCol = texture( iChannel0, uv ).rgba;
    inputCol = mix( inputCol, vec4(1.0), inputCol.g );

    float imageDistort = inputCol.r * 0.1;


    // apply rotations
    vec2 rotatedUv = uv;
    rotatedUv -= 0.5;
    //rotatedUv = rotate2D( 3.145 ) * rotatedUv;
    rotatedUv += 0.5;

    // apply scale    
    float scaleValue = 1.0; 
    vec2 scaledUv = rotatedUv;
    scaledUv -= 0.5;
    scaledUv *= clamp( scaledUv * scaleValue, 0.0, 1.0 );
    scaledUv += 0.5;

    // apply offsets

    vec2 uvDistorted = uv + vec2( imageDistort );
    float bias = 1.0;
    vec4 imageCol = texture( iChannel1, uvDistorted, bias ).rgba;
    imageCol.a = imageCol.r;

    // Output to screen
    fragColor = inputCol + (imageCol.rrrr * 1.0 - inputCol.g);
}
