void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;
    for(float i=1.0;i<9.5;i+=1.0){
        float angle= mod(iTime*1.5+i*0.01,3.14);
        float newX=(uv.x-0.5)/cos(angle)+0.5;
    // Time varying pixel color
        if(newX>0.999||newX<0.001){
            continue;
        }
        if(angle<3.1415/2.0)
            col += texture(iChannel0,vec2(newX,uv.y)).rgb;
        else
            col += texture(iChannel1,vec2(newX,uv.y)).rgb;
    }
    col/=9.0;
    // Output to screen
    fragColor = vec4(col,1.0);
}
