// Code by Flopine

// Thanks to wsmind, leon, XT95, lsdlive, lamogui, 
// Coyhot, Alkama,YX, NuSan, slerpy, wwrighter 
// BigWings, FabriceNeyret and Blackle for teaching me

// Thanks LJ for giving me the spark :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and other to sprout :)  
// https://twitter.com/CookieDemoparty

#define PI acos(-1.)
#define TAU (2.*PI)

#define hr (vec2(sqrt(3.), 1.)*.5)
#define hd(u) max(abs(u.y),dot(abs(u), normalize(hr)))
#define AAstep(thre, val) smoothstep(-.7,.7,(val-thre)/min(0.02,fwidth(val-thre)))

#define noise(u) textureLod(iChannel0, u, 0.).x


float moda (inout vec2 p, float rep)
{
    float per = TAU/rep;
    float a = atan(p.y,p.x)+per*.5;
    float c = floor(a/per);
    a = mod(a-per*.5,per)-per*.5;
    if (abs(c) >= (rep*.5)) c = abs(c);
    p = vec2(cos(a), sin(a))*length(p);
    return c;
}

vec4 hexgrid (vec2 p)
{
    vec2 ga=mod(p,hr*2.)-hr, gb=mod(p-hr,hr*2.)-hr,
    guv=(dot(ga,ga)<dot(gb,gb))?ga:gb,
    gid=p-guv;
    return vec4(guv,gid);
}

vec3 frame (vec2 uv)
{
    uv.y += iTime;
    vec4 hg = hexgrid(uv);
    vec2 uu; float m, aid;
    if(noise((hg.zw+iTime*0.004)*.2)<.5) 
    {
        hg.xy *= 3.;
        vec4 ng = hexgrid(hg.xy);

        uu = ng.xy;
        aid = moda(uu, 3.);
        m = AAstep(0.46,hd(ng.xy)) + (1.-AAstep(0.03, abs(uu.y)));
        return (mix(vec3(0.05,0.2,0.1), 
                    vec3(noise(ng.zw*.3),1.,noise(ng.zw*.6)*.5), 
                    aid*.5+.5
                    )
                    +m)*.5;
    }
    else 
    { 
        uu = hg.xy;
        aid = moda(uu, 3.);
        m = AAstep(0.4,hd(hg.xy))+(1.-AAstep(0.2, abs(uu.y)));
        return mix(vec3(0.05,0.2,0.1), 
                    vec3(noise(hg.zw*.3),1.,noise(hg.zw)*.5), 
                    aid*.5+.5
                    )
                    *(m);
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    uv *= 3.;

    vec3 col = vec3(frame(uv));

    fragColor = vec4(sqrt(col),1.0);
}
