void UI( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 ifc = ivec2(fragCoord);
    fragColor = texelFetch(iChannel0, ifc, 0);

    if (ifc.y > 0 || ifc.x > 4) return;

    int nr = min(0, iFrame);

    // (0,0) = centerx (hi 16 bits of each channel to reconstruct 2 floats)
    // (1,0) = centery (hi 16 bits of each channel to reconstruct 2 floats)
    // (2,0) = size    (hi 16 bits of each channel to reconstruct 2 floats)
    // (3,0) = (iters (sign=hud), samples, date(secs), view) (int, stored as float)
    // (4,0) = (iResolution.x, iResolution.y, blockStart, blockEnd)

    vec4 center_x_v4 = texelFetch(iChannel0, ivec2(0, 0), 0);
    vec4 center_y_v4 = texelFetch(iChannel0, ivec2(1, 0), 0);
    vec4 vsize_v4    = texelFetch(iChannel0, ivec2(2, 0), 0);
    vec4 opts        = texelFetch(iChannel0, ivec2(3, 0), 0); 
    vec4 res         = texelFetch(iChannel0, ivec2(4, 0), 0);

    int iters   = int(abs(opts.x));
    int samples = int(opts.y);
    int hud     = (opts.x > 0.0) ? 1 : -1;  // int(sign(opts.x)); // int(sign(x)) is broken?
    int view    = int(opts.w);

    vec2 blockId = res.zw;

    bool restartRender = false;

    // restart render if the resolution changes (fullscreen, etc.)
    if (ivec2(res.xy) != ivec2(iResolution.xy)) restartRender = true;

    uvec2 center_x = v4_to_u2(center_x_v4);
    uvec2 center_y = v4_to_u2(center_y_v4);
    uvec2 vsize    = v4_to_u2(vsize_v4);

    bool isLtKeyDown    = texelFetch(iChannel1, ivec2(LT_KEY, 1), 0).x > 0.;
    bool isUpKeyDown    = texelFetch(iChannel1, ivec2(UP_KEY, 1), 0).x > 0.;
    bool isRtKeyDown    = texelFetch(iChannel1, ivec2(RT_KEY, 1), 0).x > 0.;
    bool isDnKeyDown    = texelFetch(iChannel1, ivec2(DN_KEY, 1), 0).x > 0.;

    bool isIKeyPressed  = texelFetch(iChannel1, ivec2(I_KEY, 1), 0).x > 0.;
    bool isDKeyToggled  = texelFetch(iChannel1, ivec2(D_KEY, 2), 0).x > 0.;
    bool isRKeyDown     = texelFetch(iChannel1, ivec2(R_KEY, 1), 0).x > 0.;
    bool isSKeyPressed  = texelFetch(iChannel1, ivec2(S_KEY, 1), 0).x > 0.;
    bool isVKeyPressed  = texelFetch(iChannel1, ivec2(V_KEY, 1), 0).x > 0.;
    bool isXKeyDown     = texelFetch(iChannel1, ivec2(X_KEY, 1), 0).x > 0.;
    bool isZKeyDown     = texelFetch(iChannel1, ivec2(Z_KEY, 1), 0).x > 0.;
    bool isShiftKeyDown = texelFetch(iChannel1, ivec2(SHIFT_KEY, 0), 0).x > 0.;

    bool isMoveKeyDown  = isLtKeyDown || isUpKeyDown || isRtKeyDown || isDnKeyDown || isXKeyDown || isZKeyDown;
    bool isZoomKeyDown  = isShiftKeyDown || isXKeyDown || isZKeyDown;

    uvec2[2] newCenter = uvec2[2]( center_x, center_y );
    uvec2 newVSize     = vsize;

    if (iFrame == 0 || isRKeyDown) {
        newCenter[0] = float_to_u2(-0.5);
        newCenter[1] = float_to_u2(0.);
        newVSize = float_to_u2(1.);
        iters = 256;
        samples = 1;
        view = -1;
        restartRender = true;
    }

    if (isMoveKeyDown) {
        if (isZoomKeyDown) {
            float zoom = 2.;
            bool zoomin = isUpKeyDown || isZKeyDown;
            bool zoomout = isDnKeyDown || isXKeyDown;
            if (zoomin || zoomout) {
                if (zoomin) zoom = 1. / zoom;
                newVSize = u2_mul(vsize, float_to_u2(zoom));
            }
            if (u2_to_float(newVSize) > 1.5) newVSize = float_to_u2(1.5);
            restartRender = true;
        } else {
            float move = 0.2;
            bool horiz = isLtKeyDown || isRtKeyDown;
            uvec2 a = (horiz) ? newCenter[0] : newCenter[1];
            if (isLtKeyDown || isDnKeyDown) move = -move;
            uvec2 b = u2_add( a, u2_mul(newVSize, float_to_u2( move)));
            if (horiz) { newCenter[0] = b; } else { newCenter[1] = b; }
            #if 0
            if (isLtKeyDown) newCenter[0] = u2_add( newCenter[0], u2_mul(newVSize, float_to_u2( move)));
            if (isRtKeyDown) newCenter[0] = u2_add( newCenter[0], u2_mul(newVSize, float_to_u2( move)));
            if (isUpKeyDown) newCenter[1] = u2_add( newCenter[1], u2_mul(newVSize, float_to_u2( move)));
            if (isDnKeyDown) newCenter[1] = u2_add( newCenter[1], u2_mul(newVSize, float_to_u2( move)));
            #endif
            restartRender = true;
        }
    }

    if (isIKeyPressed) {
        if (isShiftKeyDown) iters = max(int(float(iters) * 1.1), iters+1);
        else                iters = max(1, min(int(float(iters) / 1.1), iters-1));
        restartRender = true;
    }

    if (isSKeyPressed) {
        if (isShiftKeyDown) samples++;
        else                samples = max(1, samples-1);
        restartRender = true;
    }

    int newHud = isDKeyToggled ? 1 : -1;
    //if (hud != newHud) restartRender = true;

    if (isVKeyPressed) {

    restartRender = true;
    view = view + 1;
    if (view > 8) view = 0;

    uint[] views = uint[] (
        // case 0:
        0x3f5ff080u, 0x3f7b7d00u, 0x3f2cde00u, 0x3f492480u,
        0x3f1ff200u, 0x3f49f780u, 0x3f350e00u, 0x3f541280u,
        0x3f1e7c00u, 0x3f000000u, 0x3f000000u, 0x3f000000u,
        300u, 1u, // iters, samples

        // case 1:
        0x3f5ff080u, 0x3f7b7d00u, 0x3f2cde00u, 0x3f492480u,
        0x3f1ff200u, 0x3f49f780u, 0x3f350e00u, 0x3f541280u,
        0x3f1e7c00u, 0x3f000000u, 0x3f000000u, 0x3f000000u,
        300u, 1u, // iters, samples

        // case 2:
        0x3f5ff080u, 0x3f7b7d00u, 0x3f2cde00u, 0x3f492480u,
        0x3f1ff200u, 0x3f49f780u, 0x3f350e00u, 0x3f541280u,
        0x3f1e7c00u, 0x3f000000u, 0x3f000000u, 0x3f000000u,
        700u, 1u, // iters, samples

        // case 3: deep spirals
        0x3f5ff380u, 0x3f7fc000u, 0x3f48e800u, 0x3f25d600u,
        0x3f1fd280u, 0x3f2a8500u, 0x3f31ff80u, 0x3f74da80u,
        0x3f1ea780u, 0x3f0cd280u, 0x3f000000u, 0x3f000000u,
        2000u, 1u, // iters, samples

        // case 4:
        0x3f5ff080u, 0x3f69ca00u, 0x3f2eb180u, 0x3f589580u,
        0x3f1ff200u, 0x3f2a5500u, 0x3f162500u, 0x3f5df400u,
        0x3f1f3800u, 0x3f000000u, 0x3f000000u, 0x3f000000u,
        3500u, 1u, // iters, samples

        // case 5: 3-way lightning
        0x3f5ffe00u, 0x3f21c500u, 0x3f1bad00u, 0x3f2aa200u,
        0x3f1fd280u, 0x3f30e980u, 0x3f4f9480u, 0x3f0dcd00u,
        0x3f1eb000u, 0x3f000000u, 0x3f000000u, 0x3f000000u,
        1385u, 1u, // iters, samples

        // case 6: sea horses
        0x3f5fbd80u, 0x3f748180u, 0x3f40dd80u, 0x3f6f4000u,
        0x3f1ff480u, 0x3f6de800u, 0x3f493980u, 0x3f333200u,
        0x3f1eac00u, 0x3f000000u, 0x3f000000u, 0x3f000000u,
        16000u, 1u, // iters, samples

        // case 7:
        0x3f5ffe00u, 0x3f37e400u, 0x3f6f5100u, 0x3f2ffb80u,
        0x3f5fa680u, 0x3f723c80u, 0x3f2cfa80u, 0x3f014000u,
        0x3f1f5c00u, 0x3f000000u, 0x3f000000u, 0x3f000000u,
        2250u, 1u, // iters, samples

        // case 8:
        0x3f5ffb80u, 0x3f7fcc80u, 0x3f464800u, 0x3f02be80u,
        0x3f1f5a80u, 0x3f343780u, 0x3f2e4f80u, 0x3f577c80u,
        0x3f1e9800u, 0x3f000000u, 0x3f000000u, 0x3f000000u,
        1158u, 1u // iters, samples
    );

    switch(view) {
    case 0:
        // This predefined view is as close as I can get with double-precision 
        // to the view in TekF's shader here https://www.shadertoy.com/view/XdsXWN
        // TekF's shader gets significantly better than double-precision for this specific view!

        // double-precision bits
        // newCenter[0].x = 0x3fd451f0u; newCenter[0].y = 0x11c41c40u;
        // u4_to_u2 takes encoded coordinates, as displayed in the HUD
        newCenter[0] = u4_to_u2(uvec4(0x3f1fea00u, 0x3f28f800u, 0x3f08e200u, 0x3f0e2000u));

        //newCenter[1].x = 0xbfdf5c28u; newCenter[1].y = 0x864005fcu;
        newCenter[1] = u4_to_u2(uvec4(0x3f5fef80u, 0x3f2e1400u, 0x3f432000u, 0x3f02fe00u));

        //newVSize.x = 0x3cc201d0u; newVSize.y = 0xc7e7df45u;
        newVSize = u4_to_u2(uvec4(0x3f1e7580u, 0x3f000000u, 0x3f000000u, 0x3f000000u));
        iters = 300;
        break;

    case 1:
        // center of a 5-pronged star
        newCenter[0] = uvec2( 0xbfe1fd90u, 0x5a255758u );
        newCenter[1] = uvec2( 0x3fe491f5u, 0x45126abcu );
        newVSize     = uvec2( 0x3d244b79u, 0u);
        iters = 300;
        samples = 1;
        break;

    default:
        int i = view * 14;
        newCenter[0] = u4_to_u2(uvec4(views[i+0], views[i+1], views[i+2], views[i+3]));
        newCenter[1] = u4_to_u2(uvec4(views[i+4], views[i+5], views[i+6], views[i+7]));
        newVSize     = u4_to_u2(uvec4(views[i+8], views[i+9], views[i+10], views[i+11]));
        iters = int(views[i+12]);
        samples = int(views[i+13]);

    }
    }

    // Click to center the clicked point. 
    if (iMouse.w > 0.) {
    // Safe zone in the bottom left corner so
    // I can focus the window without moving the view
    if (iMouse.z > 50. || iMouse.w > 50.) {
        float aspect = iResolution.y / iResolution.x;
        vec2 uv = iMouse.xy / iResolution.xy;

        // map the mouse click to our complex number coordinate
        // and store it as our new center

        uvec2[2] center = uvec2[2]( center_x, center_y );
        uvec2[2] size   = uvec2[2]( vsize, vsize);
        size[0] = u2_mul( size[0], float_to_u2(1./aspect) );

        vec2 wuv = 2. * uv - 1.;
        for (int i = nr; i < 2; i++) {
            uvec2 suv = u2_mul( size[i], float_to_u2(wuv[i]) );
            newCenter[i] = u2_add( center[i], suv );
        }

        restartRender = true;
    }
    }


    if (restartRender) {
        blockId = vec2(0, MINBLOCKS);
    } else 
    {
        float dt = iDate.w - opts.z; // seconds since last frame 

        float prevNumBlocks = blockId.y - blockId.x;
        float newNumBlocks;

        // adjust upward slowly, and adjust downward QUICKLY
        if      (dt < 0.05) newNumBlocks = prevNumBlocks + float(BLOCKINC);
        else if (dt > 0.20) newNumBlocks = float(MINBLOCKS);
        else if (dt > 0.10) newNumBlocks = prevNumBlocks / 2.;
        else                newNumBlocks = prevNumBlocks;

        newNumBlocks = clamp(newNumBlocks, float(MINBLOCKS), float(MAXBLOCKS));

        blockId = vec2(blockId.y, blockId.y + newNumBlocks);
    }

    switch(ifc.x) {
        case 0: fragColor = u2_to_v4(newCenter[0]); break;
        case 1: fragColor = u2_to_v4(newCenter[1]); break;
        case 2: fragColor = u2_to_v4(newVSize); break;
        case 3: fragColor = vec4(newHud * iters, samples, iDate.w, view); break;
        case 4: fragColor = vec4(iResolution.xy, vec2(blockId)); break;
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (fragCoord.y < 1. && fragCoord.x < 5.) {
        UI(fragColor, fragCoord);
    } 
}

#define INCREMENTAL     // render one block at a time
#define SHOW_NEXT_BLOCK // display active block
const int BLOCKSIZE = 64;
const int MINBLOCKS = 1; // min # of blocks to render at once. ideally minBlocks * BLOCKSIZE^2 == # GPU thread cores
const int MAXBLOCKS = MINBLOCKS * 8;
const int BLOCKINC  = 2; // # blocks to increase at a time based on framerate
//#define SHOW_COBWEBS    // visualize a little bit of internal set structure (with a bug I accidentally discovered Pickover Stalks)

vec4 premult(vec4 color) {
    return vec4(color.rgb * color.a, color.a);
}

vec4 comp(vec4 bg, vec4 fg) {
    return fg + bg * (1. - fg.a);
}


#define MASK(nbits) ((1u << (nbits)) - 1u)
const uint mask13 = MASK(13);

const int U2_SIGN_BITS    = 1;
const int U2_EXP_BITS     = 11;
const int U2_SIGNEXP_BITS = U2_SIGN_BITS + U2_EXP_BITS;
const int U2_BIAS         = int(MASK( U2_EXP_BITS - 1 ));  // 1023
const int U2_MANT_BITS    = 64 - U2_SIGNEXP_BITS;
const int U2_EXP_POS      = 32 - U2_SIGNEXP_BITS; // position of exponent in the hi 32 bit word

const uint U2_SIGN_MASK = 1u << 31; // 0x80000000
const uint U2_EXP_MASK  = MASK(U2_EXP_BITS) << U2_EXP_POS; // 0x7ff00000

uvec2 u2_mul(uvec2 a, uvec2 b) {
  uvec2 c = uvec2(0,0); // c = a * b

  int aexp = int((a.x & U2_EXP_MASK) >> 20);
  int bexp = int((b.x & U2_EXP_MASK) >> 20);
  if (aexp == 0 || bexp == 0) return c; // TODO: handle denormals?
  aexp -= 1023;
  bexp -= 1023;
  int cexp = aexp + bexp; 
  // if (cexp + U2_BIAS <= 0 || cexp + U2_BIAS >= 256) return c; // overflow check isn't helpful for Mandelbrot

  uint asign = a.x & U2_SIGN_MASK;
  uint bsign = b.x & U2_SIGN_MASK;
  uint csign = asign ^ bsign;

  // extract mantissa 13 bits at a time
  // LSB to MSB order, so amant[0] is lo bits
  uint amant[4];
  amant[0] = ((a.y      ) & mask13);
  amant[1] = ((a.y >> 13) & mask13);
  amant[2] = ((a.y >> 26) & mask13) | ((a.x << 6) & mask13);
  amant[3] = ((a.x >>  7) & mask13) | 0x00002000u;

  uint bmant[4];
  bmant[0] = ((b.y      ) & mask13);
  bmant[1] = ((b.y >> 13) & mask13);
  bmant[2] = ((b.y >> 26) & mask13) | ((b.x << 6) & mask13);
  bmant[3] = ((b.x >>  7) & mask13) | 0x00002000u;

  // multiply
  uint cmant[9];
  for (int i = 0; i < 9; i++) cmant[i] = 0u;

  // starting from here, avg rel err =~ 8e-17
  #if 0
  cmant[0+0] += amant[0] * bmant[0];

  // starting from here, avg rel err =~ 8e-17
  cmant[0+1] += amant[0] * bmant[1];
  cmant[1+0] += amant[1] * bmant[0];

  // starting from here, avg rel err =~ 8e-17
  cmant[0+2] += amant[0] * bmant[2];
  cmant[1+1] += amant[1] * bmant[1];
  cmant[2+0] += amant[2] * bmant[0];
  #endif

  // starting from here, avg rel err =~ 1.7e-16
  cmant[1+2] += amant[1] * bmant[2];
  cmant[2+1] += amant[2] * bmant[1];
  // starting from here, avg rel err =~ 4.9e-13
  cmant[0+3] += amant[0] * bmant[3];
  cmant[3+0] += amant[3] * bmant[0];

  // starting from here, avg rel err =~ 1.8e-12
  cmant[1+3] += amant[1] * bmant[3];
  cmant[2+2] += amant[2] * bmant[2];
  cmant[3+1] += amant[3] * bmant[1];

  // starting from here, avg rel err =~ 1.3e-08
  cmant[2+3] += amant[2] * bmant[3];
  cmant[3+2] += amant[3] * bmant[2];

  // starting from here, avg rel err =~ 9.0e-05
  cmant[3+3] += amant[3] * bmant[3];


  // carry
  for (int i = 3; i < 9; i++) {
    cmant[i] += (cmant[i-1] & 0xffffe000u) >> 13;
    cmant[i-1] &= 0x00001fffu;
  }

  // find high bit set
  int hibit = ((cmant[8] & 2u) != 0u) ? 1 : 0;

  cexp += hibit;

  if (hibit == 1) c.x |= (cmant[8] & 1u) << 19;
  c.x |= cmant[7] << (7-hibit);
  c.x |= cmant[6] >> (6+hibit);

  c.y |= cmant[6] << (26-hibit);
  c.y |= cmant[5] << (13-hibit);
  c.y |= cmant[4] >> (0+hibit);

  c.x |= csign;
  c.x |= (uint(cexp + 1023) << 20u) & U2_EXP_MASK;

  return c;
}

uvec2 u2_add(uvec2 a, uvec2 b) 
{
  int aexp = int((a.x & U2_EXP_MASK) >> 20);
  int bexp = int((b.x & U2_EXP_MASK) >> 20);
  if (aexp == 0 && bexp == 0) return uvec2(0); // TODO: handle denormals
  if (aexp == 0) return b;
  if (bexp == 0) return a;

  // sort so that a is the larger exponent
  if (bexp > aexp) {
    uvec2 utmp = a;
    a = b;
    b = utmp;

    int itmp = aexp;
    aexp = bexp;
    bexp = itmp;
  }

  // remove bias, for a signed exponent value
  aexp -= 1023;
  bexp -= 1023;

  int aexpDelta = aexp - bexp;
  if (aexpDelta > 52) return a;

  uint asign = a.x & U2_SIGN_MASK;
  uint bsign = b.x & U2_SIGN_MASK;

  // extract mantissa 26 bits at a time
  // LSB to MSB order, so amant[0] is lo bits
  uint amant_lo, amant_hi, bmant_lo, bmant_hi;
  amant_hi = 0x04000000u | ((a.x << 6) & 0x03ffffc0u) | ((a.y >> 26) & 0x0000003fu); // include explicit leading '1', for 27 bits total
  amant_lo = a.y << 6; // 26 bits (TODO: guard/round/sticky?)

  bmant_hi = 0x04000000u | ((b.x << 6) & 0x03ffffc0u) | ((b.y >> 26) & 0x0000003fu); // include explicit leading '1', for 27 bits total
  bmant_lo = b.y << 6; // 26 bits (TODO: guard/round/sticky?)

  // shift b into a's range
  if (aexpDelta > 0) {
    if (aexpDelta < 32) {
        bmant_lo = (bmant_hi << (32 - aexpDelta)) | (bmant_lo >> aexpDelta);
        bmant_hi >>= aexpDelta;
    } else {
        bmant_lo = bmant_hi >> (aexpDelta - 32);
        bmant_hi = 0u;
    }
  }

  if (asign != 0u) {
    amant_hi = ~amant_hi; 
    amant_lo = (~amant_lo + (1u<<6)) & 0xffffffc0u;
    // carry
    if (amant_lo == 0u) amant_hi++;
  }
  if (bsign != 0u) { 
    bmant_hi = ~bmant_hi; 
    bmant_lo = (~bmant_lo + (1u<<6)) & 0xffffffc0u;
    // carry
    if (bmant_lo == 0u) bmant_hi++;
  }

  // add
  uint cmant_lo=0u, cmant_hi=0u;

  cmant_hi = amant_hi + bmant_hi;
  cmant_lo = amant_lo + bmant_lo;
  // carry
  if (cmant_lo < amant_lo) cmant_hi++;

  uint csign = cmant_hi & U2_SIGN_MASK;

  if (csign != 0u) {
    cmant_hi = ~cmant_hi;
    cmant_lo = (~cmant_lo + (1u<<6)) & 0xffffffc0u;
    // carry
    if (cmant_lo == 0u) cmant_hi++;
  }

  // find high bit set
  int hibitPos = int(log2( (cmant_hi != 0u) ? float(cmant_hi) : float(cmant_lo) ));
  if (hibitPos < 0) return a;

  int cexpDelta = (cmant_hi != 0u) ? 26 - hibitPos : 26 + (32 - hibitPos);
  int cexp = aexp - cexpDelta;

  if (cexpDelta < 0) {
    // shift right by one
    cmant_lo = ((cmant_hi & 1u) << 31) | (cmant_lo >> 1);
    cmant_hi >>= 1;
  } else if (cexpDelta > 0) {
    // shift left by cexpDelta
    if (cexpDelta < 26) {
        cmant_hi = (cmant_hi << cexpDelta) | (cmant_lo >> (32 - cexpDelta));
        cmant_lo <<= cexpDelta;
    }
    else {
        cmant_hi = (cmant_lo >> 6) << (cexpDelta - 26);
        cmant_lo = 0u;
    }
  }

  uvec2 c = uvec2(0,0); // result

  c.x |= csign;
  c.x |= uint((cexp + 1023) << 20) & U2_EXP_MASK;

  c.x |= (cmant_hi >> 6) & 0x000fffffu;
  c.y |= ((cmant_hi << (32-6)) & 0xfc000000u) | ((cmant_lo >> 6) & 0x03ffffffu);

  return c;
}

uvec2 float_to_u2(float f) {
    uint fbits = floatBitsToUint(f);

    uint sign =      fbits & U2_SIGN_MASK;
    int   exp = int((fbits & 0x7f800000u) >> 23);
    if (exp == 0) return uvec2(0,0); // denormal
    exp -= 127;
    uint mant =      fbits & 0x007fffffu;

    uvec2 u2;
    u2.x = sign | (uint(exp + 1023) << 20) | (mant >> 3);
    u2.y = fbits << (32 - 3);
    return u2;
}

float u2_to_float(uvec2 u2) {

    uint sign =      u2.x & U2_SIGN_MASK;
    int   exp = int((u2.x & U2_EXP_MASK) >> 20) - 1023;
    uint mant = ((u2.x & 0x000fffffu) << 3) | ((u2.y & 0xe0000000u) >> 29);

    uint fbits = sign | (uint(exp + 127) << 23) | mant;
    return uintBitsToFloat(fbits);
}

uvec2 u2_neg(uvec2 u2) {
    return uvec2(u2.x ^ U2_SIGN_MASK, u2.y);
}

uvec2 u2(float f) { return float_to_u2(f); }

/*
*/

/*

Encode a U2 into our texture buffer

Use the top 16 bits of the mantissa in each channel to capture our data

4 channels * 16 bits = a 64 bit number per pixel

My idea here is an attempt to accurately capture all bits, 
even on devices with low precision textures. 

It does seem to work on my desktop machine to store uints directly as full floats,
but that seems risky as I could occasionally end up with NaNs or denormals and they
might not decode accurately.

Not sure if this encoding is misguided, because I don't know how webGL 
precision really works. How many bits do the ShaderToy Buffers guarantee, 
and which bits are they?

*/

vec4 u2_to_v4(uvec2 u2) {
    uint real_hi = (u2.x >> 16) & 0x0000ffffu;
    uint real_lo = (u2.x >>  0) & 0x0000ffffu;
    uint imag_hi = (u2.y >> 16) & 0x0000ffffu;
    uint imag_lo = (u2.y >>  0) & 0x0000ffffu;

    vec4 v4 = vec4(0);
    v4.x = uintBitsToFloat((real_hi << (23-16)) | 0x3f000000u);
    v4.y = uintBitsToFloat((real_lo << (23-16)) | 0x3f000000u);
    v4.z = uintBitsToFloat((imag_hi << (23-16)) | 0x3f000000u);
    v4.w = uintBitsToFloat((imag_lo << (23-16)) | 0x3f000000u);
    return v4;
}

// Decode a v4 (pixel from the buffer) into a U2
uvec2 v4_to_u2(vec4 v4) {
    uint real_hi = (floatBitsToUint(v4.x) >> (23-16)) & 0x0000ffffu;
    uint real_lo = (floatBitsToUint(v4.y) >> (23-16)) & 0x0000ffffu;
    uint imag_hi = (floatBitsToUint(v4.z) >> (23-16)) & 0x0000ffffu;
    uint imag_lo = (floatBitsToUint(v4.w) >> (23-16)) & 0x0000ffffu;

    uvec2 u2;
    u2.x = (real_hi << 16) | real_lo;
    u2.y = (imag_hi << 16) | imag_lo;
    return u2;
}

// This is purely to allow input of the float-encoded bits
// Same as v4_to_u2 but without the float bits conversion
// See the handling of '1' and '2' keys in the UI() function
uvec2 u4_to_u2(uvec4 u4) {
    uint real_hi = ((u4.x) >> (23-16)) & 0x0000ffffu;
    uint real_lo = ((u4.y) >> (23-16)) & 0x0000ffffu;
    uint imag_hi = ((u4.z) >> (23-16)) & 0x0000ffffu;
    uint imag_lo = ((u4.w) >> (23-16)) & 0x0000ffffu;

    uvec2 u2;
    u2.x = (real_hi << 16) | real_lo;
    u2.y = (imag_hi << 16) | imag_lo;
    return u2;
}


float v4_to_float(vec4 v4) { return v4.x; }
vec4  float_to_v4(float f) { return vec4(f,0,0,0); }


